url = "https://aboutpet.co.kr"
formulas_list = []
for link in sales_list:
    diction = dict()
    driver.get(url + link)
    diction['id'] = len(formulas_list)
    diction['Manufacturer'] = driver.find_element(By.CLASS_NAME, 'pstore').text
    diction['name_product'] = driver.find_element(By.CLASS_NAME, 'names').text
    diction['image'] = driver.find_element(By.XPATH, '//*[@id="contents"]/div[1]/section[1]/div[2]/div[2]/ul/li/a/span/img').get_attribute('src')
    if any(number in diction['name_product'] for number in ['2종', '3종', '4종', '5종', '6종', '7종', '8종', '9종']):
        continue
    diction['price'] = driver.find_element(By.CSS_SELECTOR, '#contents > div.pdTops > section.pdInfos.p1 > div.prices > div.price > span.prcs').text.replace('원', '' ).replace(',', '')
    driver.execute_script("document.querySelector('#contents > div.pdCtns > div.tabCtns > section.sec.inf').classList.add('open');")
    table_row = driver.find_elements(By.CSS_SELECTOR, '#contentPc > div.gdtblset > div.gdtbl.a > table > tbody > tr')
    analysis = {}
    for row in table_row:
        head = row.find_element(By.TAG_NAME, 'th').text
        value = row.text.split()[1:]
        analysis[head] = value
    if analysis:
        diction['analysis NMDM'] = analysis
    else:
        diction['analysis NMDM'] = "No Information"
    ingredient_h = driver.find_elements(By.XPATH, '//*[@id="contentPc"]/div[1]/div[2]/table/tbody/tr/th')
    ingredient_d = driver.find_elements(By.XPATH, '//*[@id="contentPc"]/div[1]/div[2]/table/tbody/tr/td')
    for head, data in zip(ingredient_h, ingredient_d):
        diction[head.text] = data.text
    info_head = driver.find_elements(By.XPATH, '//*[@id="contentPc"]/div[2]/table/tbody/tr/th')
    info_data = driver.find_elements(By.XPATH, '//*[@id="contentPc"]/div[2]/table/tbody/tr/td')
    for head, data in zip(info_head, info_data):
        diction[head.text] = data.text
    try:
        diction['point'] = driver.find_element(By.XPATH, '//*[@id="estmArea"]/div[1]/div[1]/div[1]/div[1]').text
        reviews = []
        review_messages = driver.find_elements(By.CLASS_NAME, 'msgs')
        for review in review_messages:
            reviews.append(review.text)
        diction['reviews'] = reviews
    except NoSuchElementException:
        diction['reviews'] = 0
    print(diction)
    formulas_list.append(diction)
driver.quit()
with open('./aboutPet.json', 'w', encoding='utf8', newline="") as output_file:
    json.dump(formulas_list, output_file, indent=2, ensure_ascii=False)



